import{_ as t,o as r,c as i,ag as a}from"./chunks/framework.DM0yugQT.js";const p=JSON.parse('{"title":"Provider Outage Triage Quick Guide","description":"","frontmatter":{},"headers":[],"relativePath":"operations/provider-outage-triage-quick-guide.md","filePath":"operations/provider-outage-triage-quick-guide.md","lastUpdated":1771758548000}'),o={name:"operations/provider-outage-triage-quick-guide.md"};function d(l,e,c,s,n,u){return r(),i("div",null,[...e[0]||(e[0]=[a('<h1 id="provider-outage-triage-quick-guide" tabindex="-1">Provider Outage Triage Quick Guide <a class="header-anchor" href="#provider-outage-triage-quick-guide" aria-label="Permalink to &quot;Provider Outage Triage Quick Guide&quot;">​</a></h1><p>Use this quick guide when a provider starts failing or latency spikes.</p><h2 id="_5-minute-flow" tabindex="-1">5-Minute Flow <a class="header-anchor" href="#_5-minute-flow" aria-label="Permalink to &quot;5-Minute Flow&quot;">​</a></h2><ol><li>Confirm process health: <ul><li><code>curl -sS -f http://localhost:8317/health</code></li></ul></li><li>Confirm exposed models still look normal: <ul><li><code>curl -sS http://localhost:8317/v1/models -H &quot;Authorization: Bearer &lt;api-key&gt;&quot; | jq &#39;.data | length&#39;</code></li></ul></li><li>Inspect provider metrics for the failing provider: <ul><li><code>curl -sS http://localhost:8317/v1/metrics/providers | jq</code></li></ul></li><li>Check logs for repeated status codes (<code>401</code>, <code>403</code>, <code>429</code>, <code>5xx</code>).</li><li>Reroute critical traffic to fallback prefix/provider.</li></ol><h2 id="decision-hints" tabindex="-1">Decision Hints <a class="header-anchor" href="#decision-hints" aria-label="Permalink to &quot;Decision Hints&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Symptom</th><th>Likely Cause</th><th>Immediate Action</th></tr></thead><tbody><tr><td>One provider has high error ratio, others healthy</td><td>Upstream outage/degradation</td><td>Shift traffic to fallback provider prefix</td></tr><tr><td>Mostly <code>401/403</code></td><td>Expired/invalid provider auth</td><td>Run auth refresh checks and manual refresh</td></tr><tr><td>Mostly <code>429</code></td><td>Upstream throttling</td><td>Lower concurrency and shift non-critical traffic</td></tr><tr><td><code>/v1/models</code> missing expected models</td><td>Provider config/auth problem</td><td>Recheck provider block, auth file, and filters</td></tr></tbody></table><h2 id="escalation-trigger" tabindex="-1">Escalation Trigger <a class="header-anchor" href="#escalation-trigger" aria-label="Permalink to &quot;Escalation Trigger&quot;">​</a></h2><p>Escalate after 10 minutes if any one is true:</p><ul><li>No successful requests for a critical workload.</li><li>Error ratio remains above on-call threshold after reroute.</li><li>Two independent providers are simultaneously degraded.</li></ul><h2 id="related" tabindex="-1">Related <a class="header-anchor" href="#related" aria-label="Permalink to &quot;Related&quot;">​</a></h2><ul><li><a href="./critical-endpoints-curl-pack">Critical Endpoints Curl Pack</a></li><li><a href="./checks-owner-responder-map">Checks-to-Owner Responder Map</a></li></ul><hr><p>Last reviewed: <code>2026-02-21</code><br> Owner: <code>Platform On-Call</code><br> Pattern: <code>YYYY-MM-DD</code></p>',13)])])}const f=t(o,[["render",d]]);export{p as __pageData,f as default};
